### IMPORT

# Python libraries import
import nltk
import re


### FUNCTIONS

# Turns all the elements of a list into lower case
def lower_list(liste):
    res = []
    for a in liste:
        res.append(a.lower())
    return res

# Given a list and a set of elements (another list), find the items in the first list that can be found in the second
def catch_words(liste, words):
    res = []
    for a in liste:
        if a.lower() in words:
            res.append(a)
    return res

# Given a list of tokens and a specific word appearing in the list, returns the half of the list that is after the word
def cut_after(tok, word):
    res = []
    b = False
    for t in tok:
        if t==word:
            b = True
        else:
            if b:
                res.append(t)
    return res

# This function formats a given sentence around numbers to have always the same writing
# 1) A number split in spaces should be merged in one big series of figures : 100 000 --> 100000
# 2) A figure should only be followed by another figure (in a number not finished yet) or a space or a point.
# Everyelse is banned and a space has to be addes : "100k" --> "100 k"
def normalize_figures(sent):
    punct = ['.', ' ']
    n = len(sent)
    res = ''
    for i in range(n):
        app = 1
        # First, we try to see if a space is surrounded by 2 figures
        if (i>0 and i<n-1):
            if (sent[i] == " "):
                try :
                    a = int(sent[i-1])
                    try :
                        b = int(sent[i+1])
                        app = 0
                    except:
                        pass
                except:
                    pass
            # Else, we try to see if a figure is directly followed by something unaccepted (a letter or a punctuation...)
            else:
                try:
                    c = int(sent[i])
                    if sent[i+1] not in punct:
                        try :
                            d = int(sent[i+1])
                        except:
                            app = 2
                except:
                    pass

        #case app == 0 (extra space to be removed) : do nothin g

        #normal case (app == 1)
        if app == 1:
            res += sent[i]
        #figure followed by something (need an extra space) app == 2
        elif app == 2:
            res += sent[i]
            res += ' '
    return res

current =  ["now", "nowadays", "today"]
NOW = 2020

# This function transforms the sentence so that words like "today" are replaced by "in 2020"
# Because the former is not detected but the latter is

def transform_dates(sent):
    tok = nltk.word_tokenize(sent)
    res = []
    for t in tok:
        if t.lower() in current:
            res.append("in")
            res.append(str(NOW))
        else:
            res.append(t)
    sent_ = " ".join(res)
    return sent_

# This function gets the position of a word in a list of tokens
# It is useful when we have two words and we want to know which one comes first
# This function also works for words in several parts (like "South Africa" for example)

def get_index(tok, w):
    n = len(tok)
    w_tok = nltk.word_tokenize(w)
    if len(w_tok)== 1:
        for i in range(n):
            if tok[i] == w_tok[0]:
                return i
    else:
        m = len(w_tok)
        for i in range(n-m+1):
            c = True
            for k in range(m):
                if tok[i+k] != w_tok[k]:
                    c = False
                    break
            if c:
                return i

# This function tells if a list of characters is a word or not
# It is a word if it is only composed of letters and nothing else (not numbers, no punctuation)

def is_word(w):
    if(re.fullmatch('[a-z]*',w) != None):
        return True
    else:
        return False

# Given a list of scores, returns the position of the highest one

def id_max(liste):
    m = 0
    id = 0
    for i in range(len(liste)):
        if liste[i] > m:
            m = liste[i]
            id = i
    return id

